<template>
  <div :class="['search-bar',color]">
    <div class="search-box">
      <span class="search-close" v-if="isShow" @click="clear"></span>
      <input type="text" placeholder="搜索宝贝" v-model="len" @focus="hide(false)" @blur="hide(true)" @keydown.enter="search"/>
      <a class="button" @click="search">搜索</a>
    </div>
  </div>

</template>
<script>
import {mapGetters,mapActions} from 'vuex'
export default {
  props: ['color'],
  data () {
    return {
      isShow:false,
      len:''
    }
  },
  watch:{
    len: function (val){
      this.isShow = val.length > 0 ? true : false
    }
  },
  methods:{
    clear () {
      this.len = ''
    },
    fn1(){
      console.log('foucs')
    },
    search () {
      this.$http.jsonp('http://m.haimi.com/api/search/product',{
        Params: {
            ActivityID:'',
            BonusID:'',
            Status:'ON',
            page:2,
            pageSize:20,
            Subject:this.len,
            Sort:'',
            BrandID:'',
            platform:'WAP'
        },
        jsonp:'_callback'
      }).then(function(opt){
        console.log(opt)
      })
      this.$router.push({path:'/search',query:{id:this.len}})
    }
    ,
    ...mapActions(['hide'])
  }
}
</script>

<!-- Add "scoped" attribute to limit CSS to this component only -->
<style scoped lang="less">
.search-bar{
  width: 100%;
  padding: 5px;
  box-sizing:border-box;
  border-bottom:solid #ccc 1px;
  .search-box{
    height:0.3rem;
    display: -webkit-box;
    width:100%;
    -webkit-box-align:center;
    position: relative;
    .search-close{
      position: absolute;
      right: .45rem;
      width:.3rem;
      height:.3rem;
      background:url(data:image/png;base64,) center center no-repeat;
      background-size: 50%;

    }
    &:before{
      content: "";
      display: inline-block;
      width: 0.30rem;
      height: 0.30rem;
      background: url(data:image/png;base64,) center center no-repeat;
      background-size: 50%;
      position: absolute;
      top: 0;
      left: 0;
    };
  }
  input{
    display: block;
    -webkit-box-flex:1;
    height: 100%;
    padding: 0;
    margin: 0;
    border: 0;
    color:#fff;
    padding-left: 0.3rem;
  }
  /* ::-webkit-input-placeholder { WebKit browsers
    color: #999;
  } */
  .button{
    background: transparent;
    border: 0;
    height: 100%;
    padding: 0 0.1rem;
  }
  &.white{
    background: #fff;
    input{
      background: #f7f7f7;
      color:#000;
    }
    .button{
      color: #000;
    }
  }
  &.black{
    background: #242424;
    input{
      background: #000;
      color:#fff;
    }
    .button{
      color: #fff;
    }
  }
}
</style>
