<template>
	<div>
		<div class="login">
			<div class="login-title">请输入手机号码验证码直接登录</div>
			<div class="login-form">
				<form action="#">
					<div class="login-cell">
						<span>手机号：</span>
						<input type="text" v-model="len">
						<span class="form-send form-clear" v-if="isShow" @click="clear"></span>
					</div>
					<div class="login-cell">
						<span>验证码：</span>
						<input type="text" v-model="psd">
						<span class="form-send" v-if="sendSmsFlg" @click="sendSms">发送验证码</span>
						<span class="form-send-tiem" v-else >{{sendTime}}秒后重新发送</span>
					</div>
					<div class="login-submit">
						<div class="login-btn" @click="loginTo">登录</div>
					</div>
				</form>
			</div>
		</div>
	</div>
</template>
<script>
export default {
  data () {
    return {
      isShow:false,
      sendSmsFlg:true,
      sendTime:10,
      len:'',
      psd:''      
    }
  },
  watch:{
    len: function (val){
      this.isShow = val.length > 0 ? true : false
    }
  },
  methods:{
    clear () {
      this.len = ''
    },
    sendSms () {
    	/*this.$http.jsonp('http://m.haimi.com/api/member/send-quick-login-code?mobile='+this.len,{
    		Params:{
    			// mobile:this.psd,
    			platform:'WAP'
    		},
    		jsonp:'_callback'
    	}).then(function(opt){
    		console.log(opt)
    	})*/
    	this.sendSmsFlg = false
    	let codeArr = [];
    	for( var i=0; i<4; i++ ){
    		codeArr.push(this.getRandom(0,9));
    	};
    	this.smsCode = codeArr = codeArr.join("");

    	let that = this;
    	let time = setInterval(function(){
    		if( --that.sendTime == 0 ){
    			clearInterval( time )
    			that.sendTime = 60;
    			that.sendSmsFlg = true;
    		}
    	}, 1000)
    },
    getRandom (min,max) {
    	var random = Math.random();
    	return Math.floor( (max-min+1)*random+min );
    },
    loginTo () {
    	if(this.len!=""&&this.psd!=""){
    		var that = this
    		var userMsg = {
    			username:that.len,
    			phone:13091272978,
    			imgs:'images/banner.jpg'
    		}
    		var url = this.$route.query.to;
    		console.log(url)
    		window.localStorage.setItem('userMsg',JSON.stringify(userMsg))
    		if(url){
    			this.$router.push(url);
    		}else{
    			this.$router.push({path: '/Zoom',query:{userName:this.len}});
    		}
    	}

    }
  }
}	
</script>
<style lang="less">
.login{
	.login-title{
		line-height: .32rem;
		text-align: center;
		color: #991f33;
		background:#fff9f3;
	}
}
.login-form{
	padding: 0 .1rem;
	box-sizing:border-box;
	background:white;
	.login-cell{
		line-height: .32rem;
		padding-top: .15rem;
		border-bottom: 1px solid #ccc;
		display:-webkit-box;
		input{
			border:none;
			padding-left:.05rem;
			display:block;
			-webkit-box-flex:1;
			height:.32rem;
		}
		.form-send{
			color:#991f33;
		}
	}

	.login-submit{
		color:white;
		line-height: .33rem;
		margin-top: .25rem;
		background:#991f33;
		text-align: center;
	}
	.form-clear{
	  width:.3rem;
      height:.3rem;
      background:url(data:image/png;base64,) center center no-repeat;
      background-size: 50%;
      
    }
}	
</style>